package greativity;

public class zad7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=7;i>0;i--) {
			for(int m=7-i;m>0;m--) {
				System.out.print(" ");
			}
			for(int j=7-i;j<7;j++) {
				System.out.print("*");
			}
			System.out.println();
			
		}

	}

}
